var wtj = require('./index')
var trim = require('trim')

wtj.extractUrl('http://www.imdb.com/title/tt0111161/locations', {
  fields: ['data'],
  parse: function($) {
    var locations = $("#filming_locations_content").childNodes;
    for (var i=1; i<locations.length; i++) {
      return {
        location: trim(locations[i][0][0]).text(),
        remarks: trim(locations[i][1]).text()
      }
    }
  }
})
.then(function(res) {
  console.log(JSON.stringify(res, null, 2));
})
